// FIXED: Only check SUBJECT for commands (ignore spam)
const input = $input.item.json;

// Only use subject - ignore body/snippet to avoid spam
const subject = (input.subject || '').toLowerCase().trim();

// Check if subject contains "command" keyword
if (!subject.includes('command') && !subject.includes('mechtech')) {
  // Not a command email - skip it
  return [];
}

// Extract command from subject
const text = subject.replace('command:', '').replace('mechtech:', '').trim();

let action = 'unknown';
let params = {};

if (text.includes('status')) {
  action = 'get_status';
} else if (text.includes('pause')) {
  action = 'pause_workflow';
} else if (text.includes('resume')) {
  action = 'resume_workflow';
} else if (text.includes('report')) {
  action = 'send_report';
} else if (text.includes('help')) {
  action = 'show_help';
} else if (text.includes('optimize')) {
  action = 'run_optimization';
  // Check which shop
  if (text.includes('shop1') || text.includes('siliconedolls24')) {
    params.shop = 'shop1';
  } else if (text.includes('shop2') || text.includes('dreamdoll')) {
    params.shop = 'shop2';
  } else {
    params.shop = 'both';
  }
} else {
  // Unknown command
  action = 'unknown';
}

return [{
  json: {
    action: action,
    params: params,
    original_text: text,
    source: input.from || input.sender || 'unknown',
    subject: input.subject || ''
  }
}];

